mapper.update:
        SELECT MAX(CNO)+1 FROM community
    
update community
set  bno=#{bno}
,    cno=#{cno}
, img_c1=#{img_c1}
, img_c2=#{img_c2}
, img_c3=#{img_c3}
, img_c4=#{img_c4}
, img_c5=#{img_c5}
    where bno=#{bno}

update board
set  bno=#{bno}
,    title=#{title}
,    mod_date_b=sysdate
,    cont_b=#{cont_b}
,    id_writer=#{id_writer}
    where bno=#{bno}

mapper.delete:
{
call
begin
delete from community where bno=#{bno};
delete from board where bno=#{bno};
end
}
