mapper.update:
	
UPDATE MEMBER
	SET PWD=#{pwd}, EMAIL=#{email}, NAME=#{name}, ZIPCODE=#{zipCode}, FIRSTADDR=#{firstAddr}, SECONADDR=#{seconAddr}, EXTRAADDR=#{extraAddr}, TEL=#{tel}, MENT=#{ment}, MOD_DATE_M=SYSDATE
	WHERE MNO=#{mno}

UPDATE PERSON
	SET FAV=#{fav}, PET=#{pet}
	WHERE MNO=#{mno}

UPDATE COMPANY
	SET BRN=#{brn}, URL_C=#{url_c}
	WHERE MNO=#{mno}
