mapper.update:
update rehome
set  bno=#{bno}
,    mno=#{mno}
,    gender=#{gender}
,    age=#{age}
,    neut=#{neut}
,    vac=#{vac}
,    cost=#{cost}
,	 img_r1=#{img_r1}
,	 img_r2=#{img_r2}
,	 img_r3=#{img_r3}
,	 img_r4=#{img_r4}
,	 img_r5=#{img_r5}
    where bno=#{bno}

update board
set  bno=#{bno}
,    title=#{title}
,    mod_date_b=sysdate
,    stmt_b=#{stmt_b}
,    cont_b=#{cont_b}
,    id_writer=#{id_writer}
    where bno=#{bno}

mapper.delete:
{
call
begin
delete from rehome where bno=#{bno};
delete from board where bno=#{bno};
end
}

mapper.update:
update payment
set  bno=#{bno}
,    id_saler=#{id_saler}
,	 id_buyer=#{id_buyer}
,    pay_date=sysdate
,    stmt_sale=1
,    stmt_buy=1
,	 sale_chk=1
,	 buy_chk=1

    where bno=#{bno}
