mapper.update:
update rehome
set  bno=#{bno}
,    rno=#{rno}
,    mno=#{mno}
,    gender=#{gender}
,    age=#{age}
,    neut=#{neut}
,    vac=#{vac}
,    cost=#{cost}
,    cat_r=#{cat_r}
    where bno=#{bno}

update board
set  type_b=#{type_b}
,    bno=#{bno}
,    title=#{title}
,    mod_date_b=sysdate
,    viewcnt=#{viewcnt}+1
,    likecnt=#{likecnt}
,    reportcnt=#{reportcnt}
,    stmt_b=#{stmt_b}
,    cont_b=#{cont_b}
,    id_writer=#{id_writer}
    where bno=#{bno}

mapper.delete:
{
call
begin
delete from rehome where bno=#{bno};
delete from board where bno=#{bno};
end
}
